const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const Cast = require('../../util/cast');
const log = require('../../util/log');
const formatMessage = require('format-message');  //多言語化のために必要

//ブロックに付けるアイコン
const blockIconURI = 'data:image/svg+xml;base64,';

//メニューに付けるアイコン
const menuIconURI = 'data:image/svg+xml;base64,';

//メニューで使う配列
const I2C_PinMenu = {
    PIN1: '1',
    PIN2: '2',
    PIN3: '3',
    PIN4: '4',
    PIN5: '5'
}
const UART_PinMenu = {
    PIN2: '2',
    PIN1: '1'
}


//クラス定義
class I2C_UART {
    constructor (runtime) {
        /**
         * The runtime instantiating this block package.
         * @type {Runtime}
         */
        this.runtime = runtime;

        //this._onTargetCreated = this._onTargetCreated.bind(this);
        //this.runtime.on('targetWasCreated', this._onTargetCreated);
    }

    //ドロップボックスメニュー (IC2_PinMenu) 
    static get I2C_PinMenu () {
        return I2C_PinMenu;
    }
    get I2C_PIN_MENU () {
        return [
            {
                text: '1',
                value: I2C_PinMenu.PIN1
            },
            {
                text: '2',
                value: I2C_PinMenu.PIN2
            },
            {
                text: '3',
                value: I2C_PinMenu.PIN3
            },
            {
                text: '4',
                value: I2C_PinMenu.PIN4
            },
            {
                text: '5',
                value: I2C_PinMenu.PIN5
            }
        ];
    }

    //ドロップボックスメニュー (UART_PinMenu) 
    static get UART_PinMenu () {
        return UART_PinMenu;
    }
    get UART_PIN_MENU () {
        return [
            {
                text: '2(txPin=17, rxPin=16)',
                value: UART_PinMenu.PIN2
            },
            {
                text: '1',
                value: UART_PinMenu.PIN1
            }
        ];
    }

    //ブロック定義
    getInfo () {
        return {
            id: 'i2cuart',
            name: formatMessage({
                id: 'i2c_uart.name',
                default: 'I2C, UART'
            }),
            menuIconURI: menuIconURI,
            blockIconURI: blockIconURI,
            blocks: [
                {
                    opcode: 'i2c_init',
                    text: formatMessage({
                        id: 'i2c_uart.i2c_init',
                        default: 'I2C-[TEXT]: Use GPIO [NUM1], [NUM2] (SCL, SDA)'
                    }),
                    blockType: BlockType.COMMAND,
                    arguments: {
                        TEXT: {
                            type: ArgumentType.STRING,
                            menu: 'i2c_pin_menu',
                            defaultValue: I2C_PinMenu.PIN1
			            },
                        NUM1: {
                            type: ArgumentType.NUMBER,
                            defaultValue: 23
                        },
			            NUM2: {
                            type: ArgumentType.NUMBER,
                            defaultValue: 22
                        }
                    }
                },
                {
                    opcode: 'i2c_write',
                    text: formatMessage({
                        id: 'i2c_uart.i2c_write',
                        default: 'OUTPUT I2C-[TEXT]: address [NUM1], command [NUM2], value [NUM3]'
                    }),		    
                    blockType: BlockType.COMMAND,
                    arguments: {
                        TEXT: {
                            type: ArgumentType.STRING,
                            menu: 'i2c_pin_menu',
                            defaultValue: I2C_PinMenu.PIN1
			            },
                        NUM1: {
                            type: ArgumentType.NUMBER,
                        },
			            NUM2: {
                            type: ArgumentType.NUMBER,
                        },
                        NUM3: {
                            type: ArgumentType.NUMBER,
                        }
                    }
                },		
                {
                    opcode: 'i2c_read',
                    text: formatMessage({
                        id: 'i2c_uart.i2c_read',
                        default: 'INPUT I2C-[TEXT]: address [NUM1], number of bytes [NUM2]'
                    }),		    		    
                    blockType: BlockType.REPORTER,
                    arguments: {
                        TEXT: {
                            type: ArgumentType.STRING,
                            menu: 'i2c_pin_menu',
                            defaultValue: I2C_PinMenu.PIN1
			            },
                        NUM1: {
                            type: ArgumentType.NUMBER,
                        },
			            NUM2: {
                            type: ArgumentType.NUMBER,
                            defaultValue: 1
                        }
                    }
                },		
                {
                    opcode: 'uart_init',
                    text: formatMessage({
                        id: 'i2c_uart.uart_init',
                        default: 'Use UART-[TEXT]',
                    }),		    		    
                    blockType: BlockType.COMMAND,
                    arguments: {
                        TEXT: {
                            type: ArgumentType.STRING,
                            menu: 'uart_pin_menu',
                            defaultValue: UART_PinMenu.PIN2
			            }
                    }
                },
                {
                    opcode: 'uart_write',
                    text: formatMessage({
                        id: 'i2c_uart.uart_write',
                        default: 'OUTPUT UART-[TEXT1]: [TEXT2]',
                    }),		    		    
                    blockType: BlockType.COMMAND,
                    arguments: {
			            TEXT1: {
                            type: ArgumentType.STRING,
                            menu: 'uart_pin_menu',
                            defaultValue: UART_PinMenu.PIN2
			            },
                        TEXT2: {
                            type: ArgumentType.STRING,
                            defaultValue: " "
                        }
                    }
                },
                {
                    opcode: 'uart_read',
                    text: formatMessage({
                        id: 'i2c_uart.uart_read',
                        default: 'INPUT I2C-[TEXT]',
                    }),		    		    
                    blockType: BlockType.REPORTER,
                    arguments: {
			            TEXT: {
                                type: ArgumentType.STRING,
                                menu: 'uart_pin_menu',
                                defaultValue: UART_PinMenu.PIN2
			            }
                    }
                }
            ],
	    //ドロップボックスメニューを使う場合は以下に定義が必要
            menus: {
                i2c_pin_menu: {
                    acceptReporters: true,
                    items: this.I2C_PIN_MENU
                },
                uart_pin_menu: {
                    acceptReporters: true,
                    items: this.UART_PIN_MENU
                },
            }
        };
    }

    // command0 ブロック．入力なし
    command0 () {
        return navigator.userAgent;
    }
    
    // value0 ブロック．入力なし
    value0 () {
        return navigator.userAgent;
    }
    
    // flag0 ブロック．入力なし
    flag0 () {
        return navigator.userAgent;
    }        

    // command1 ブロックの入力
    command1 (args) {
        const text = Cast.toString(args.TEXT);
        const num  = Cast.toString(args.NUM);
        log.log(text);
        log.log(num);
    }

    //command2 ブロックの入力
    command2 (args) { 
        const text1 = Cast.toString(args.TEXT1);
        log.log(text1);
    }

    //command3 ブロックの入力
    command3 (args) { 
        const text1 = Cast.toString(args.TEXT1);
        const num1  = Cast.toString(args.NUM1);   //toNumber でも挙動は変わらない
        log.log(args);
        log.log(text1);
        log.log(num1);
    }
}

module.exports = I2C_UART
